mapper.select:
select distinct tb_disease.disease_name as name, forecast_relevance from prediction inner join tb_disease on prediction.disease_id = tb_disease.id and
tb_disease.id != #{diseaseId} and
mirna_id = (select id from tb_mirna where mirna_name = #{mirnaName}) limit #{random},7;

select distinct tb_mirna.mirna_name as name,forecast_relevance from prediction inner join tb_mirna on prediction.mirna_id = tb_mirna.id and
tb_mirna.id != #{mirnaId} and
disease_id = (select id from tb_disease where disease_name = #{diseaseName}) limit #{random},7;
