mapper.select:
select SQL_CALC_FOUND_ROWS pmid,title,type,authors,doi,keywords,library,abs,date from tb_article where pmid in (select distinct pmid from tb_relationship where disease_id = (
select id from tb_disease where disease_name = #{diseaseName}





))

    and date >= #{startTime}


    and date < #{endTime}

limit #{pageNum},#{pageSize};
select FOUND_ROWS();

select SQL_CALC_FOUND_ROWS pmid,title,type,authors,doi,keywords,library,abs,date from tb_article where pmid in (select distinct pmid from tb_relationship where mirna_id = (
select id from tb_mirna


        and mirna_name = #{mirnaName}


))

    and date >= #{startTime}


    and date < #{endTime}

limit #{pageNum},#{pageSize};
select FOUND_ROWS();
